                            v-bind:msg="msg" 
                            v-bind:index="index" 
                            @click="readMsg(index)" 
                        ></messages-listItem>
                    </tbody>
                </table>
                <div id="reader" v-if="activeMsgShow">
                    <div class="card">
                        <div class="card-content">
