                                    v-bind:key="tag.key" 
                                    v-bind:index="index" 
                                    >
                                    <a @click="getNotesByTag(tag.key)" class="is-capitalized">
                                        {{ tag.key }}
                                        <span class="tag is-pulled-right">{{ tag.value }}</span>
                                    </a>
                                </li>
                            </ul>
