                            @click="viewNote(index)" 
                        ></productivity-note>
                    </div>
                </div>
            </div>
        </div>
        <productivity-noteview>
        </productivity-noteview>
    </div>
    `,
    data: {
        notes:[],
        tagsList: [],
        //activeNote: 0,
        activeNoteIndex: 0,
        showViewNoteModal: false
    },
    methods: {
        getNotesByTag: function (tag="No tag") {
            this.notes = [];
            loader_toggle();
            let mango = {
                            "selector": {
                                "realm": "productivity",
                                "type":"note",
                                "tags": {
                                    "$in": [tag]
                                }
                            },
                            "limit": 25,
                            "sort": [
                                { "created": "desc" }
                            ]
                        };
            if (tag == "No tag") {
                mango.selector.tags = []
            };
            fetch(url_mango, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(mango)
            })
            .then((resp) => resp.json())
            .then(function(data) {
                if (data.docs.length == 0) {
                    loader_toggle(false);
                    return
                }
                data.docs.forEach( (doc, index) => {
                    app.notes.push(doc);
                    if (index + 1 == data.docs.length) {loader_toggle(false)}
                });
            })
            .catch(function(error) {
                errorHandler(error);
            });
        },
        renderTagsList: function () {
            fetch(`${window.location.origin}/${database}/_design/pimpim/_view/note-tag-count?group=true`)
            .then((resp) => resp.json())
            .then(function(data) {
               if (data.rows.length > 0) {
                    data.rows.forEach( (aggregate) => {
                        app.tagsList.push(aggregate);
                    });
                }
            });
        },
        getLastEntriesByCount: function (count = 30) {
            this.notes = [];
            loader_toggle();
            let now = new Date().toISOString().slice(0, 16);
            let mango = {
                            "selector": {
                                "realm": "productivity",
                                "type":"note",
                                "created": { "$lte": now }
                            },
                            "limit": count,
                            "sort": [
                                { "created": "desc" }
                            ]
                        };
            fetch(url_mango, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(mango)
            })
            .then((resp) => resp.json())
            .then(function(data) {

                if (data.docs.length == 0) {
                    loader_toggle(false);
                    return
                }
                data.docs.forEach( (doc, index) => {
                    app.notes.push(doc);
                    if (index + 1 == data.docs.length) {loader_toggle(false)}
                });
            })
            .catch(function(error) {
                console.log("Looks like there was a problem: ", error);
            });
        }
    }
});

